mapper.select:
SELECT
a.name, a.id, a.mainposter,a.startdate, a.enddate,a.genre, b.showRank 
FROM  showdb as a INNER JOIN popular as b  ON a.id = b.showdb_id ORDER BY b.showRank ASC limit 0, 21;

SELECT
a.name, a.id, a.mainposter,a.startdate, a.enddate,a.genre, b.showRank 
FROM  showdb as a INNER JOIN popularW as b  ON a.id = b.showdb_id ORDER BY b.showRank ASC limit 0, 21;

SELECT
a.name, a.id, a.mainposter,a.startdate, a.enddate,a.genre, b.showRank 
FROM  showdb as a INNER JOIN popularpopularWpopularM    as b  ON a.id = b.showdb_id where genre = #{param2} ORDER BY b.showRank ASC limit 0, 5;
