mapper.select:
SELECT a.name, a.id, a.mainposter,a.startdate, a.enddate,a.genre, c.showRank FROM showdb as a INNER JOIN child as c on a.id = c.showdb_id ORDER BY c.showRank ASC limit 0, 5;
b.showRank FROM showdb as a INNER JOIN popular popularW popularM as b ON a.id = b.showdb_id
where genre = #{param2} where genre = #{param2} where genre = '클래식' or genre = '오페라' ORDER BY b.showRank ASC limit 0, 5;

select a.no, a.user_id, date_format(a.writedate, '%Y.%m.%d %h:%i') writedate, date_format(b.showDate, '%Y.%m.%d') showDate, b.showTime, c.seat_num, d.name from order_list a inner join scheduleDate as b on a.scheduleDate_id=b.id inner join seat as c on a.no=c.order_list_no inner join showdb as d on b.showdb_id=d.id
where ${booksearchKey} like '%${booksearchWord}%'
order by a.writedate desc limit ${bookonePageRecord} offset ${bookoffsetPoint}
