mapper.select:
        select a.id, a.user_id, a.title, a.content, date_format(a.writedate, '%m-%d %h:%i') writedate, b.genre
        from board a
                 inner join showdb b on a.showdb_id = b.id
        where b.genre = #{param1}
        or b.genre = #{param2}
        order by id desc;
        --         select *
--         from board

mapper.update:
update board
set title   = #{title},
    content = #{content},
    showdb_id = #{showdb_id}
where id = #{id}
